// ACTIONS
const Types = {
	TOGGLE: 'TOGGLE',
	INCREMENT: 'INCREMENT',
	DECREMENT: 'DECREMENT'
}

const Action = {
	toggleTap: () => {
		return { type: Types.TOGGLE }
	},

	incrementCount: () => {
		return { type: Types.INCREMENT }
	},

	decrementCount: () => {
		return { type: Types.DECREMENT }
	}
}

export { Types, Action };
