import { 
	createStore,
	applyMiddleware,
	compose,
	combineReducers 
} from 'redux';

import { apiMiddleware } from 'redux-api-middleware';
import { composeWithDevTools } from 'redux-devtools-extension'

import { reducer } from './reducers';

export function initializeStore (initial_state = {}, {isServer}) {
	const dev_compose = isServer ? compose : (window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose);

	const createStoreWithMiddleware = dev_compose(applyMiddleware(apiMiddleware))(	createStore);

	initial_state = {
		tap: false,
		count: 0,
	}

	return createStoreWithMiddleware(combineReducers(reducer), initial_state);
}
