import { Type } from '../actions';

// REDUCERS
export default function reducer(state = {}, action) {
	switch (action.type) {
		case Type.TOGGLE:
			return Object.assign({}, state, {
				tap: !state.tap
			})

		case Type.INCREMENT:
			return Object.assign({}, state, {
				count: state.count + 1
			})

		case Type.DECREMENT:
			return Object.assign({}, state, {
				count: state.count - 1
			})

		case `FETCH_MOVIE_DATA_SUCCESS`:
			const results = action.payload.results;

			return {
				...state,
				results: results
			}

		default:
			return state
	}
}
