import { Types } from '../actions';

// REDUCERS
export const reducer = (state = {}, action) => {
	switch (action.type) {
		case Types.TOGGLE:
			return Object.assign({}, state, {
				tap: !state.tap
			})

		case Types.INCREMENT:
			return Object.assign({}, state, {
				count: state.count + 1
			})

		case Types.DECREMENT:
			return Object.assign({}, state, {
				count: state.count - 1
			})

		default:
			return state
	}
}
